import styled from "styled-components";

import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
import { motion, useViewportScroll, useSpring, useTransform } from 'framer-motion/dist/framer-motion';
import imgL from "../../assets/image/portrait-1.png";

const ImgRight = styled.img`
  max-width: 100%;
`;

function Hero() {

  const [isComplete, setIsComplete] = useState(false);
  const { scrollYProgress } = useViewportScroll();
  const yRange = useTransform(scrollYProgress, [0, 0.9], [0, 1]);
  const pathLength = useSpring(yRange, { stiffness: 400, damping: 90 });

  useEffect(() => yRange.onChange(v => setIsComplete(v >= 1)), [yRange]);



return (

<>

      <Section hero className="position-relative">
      <svg className="progress-icon" viewBox="0 0 60 60">
        <motion.path
          fill="none"
          strokeWidth="5"
          stroke="white"
          strokeDasharray="0 1"
          d="M 0, 20 a 20, 20 0 1,0 40,0 a 20, 20 0 1,0 -40,0"
          style={{
            pathLength,
            rotate: 90,
            translateX: 5,
            translateY: 5,
            scaleX: -1 // Reverse direction of line animation
          }}
        />
        <motion.path
          fill="none"
          strokeWidth="5"
          stroke="white"
          d="M14,26 L 22,33 L 35,16"
          initial={false}
          strokeDasharray="0 1"
          animate={{ pathLength: isComplete ? 1 : 0 }}
        />
      </svg>
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Have a question <br></br>for Papa Bear?
                </Title>

                <Box className='mt-5'>
                  <a href="#questions">
                    <Button arrowRight>Check FAQ </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">
                <motion.div 
                whileTap={{ scale: 0.9 }}
                whileHover={{ 
                  scale: 1.1 }}
                className="img-main">
                  <motion.div>
                  <ImgRight 
                  src={imgL} alt="" />
                  </motion.div>
                  <ImgRight 
                  src={imgL} alt="" />
                  <ImgRight 
                  src={imgL} alt="" />
                  <ImgRight 
                  src={imgL} alt="" />
                </motion.div>
              </div>
            </Col>
          </Row>
        </Container>
      </Section>

</>

);


}


export default Hero;
