import React, {useState, useCallback} from "react";
import { motion } from 'framer-motion/dist/framer-motion';
import styles from "styled-components";

import {  Title } from "../core";

import imgL from "../../assets/favicon.png";
import { Col } from "react-bootstrap";
import { Questions } from "./Questions";

export default function App() {


    return (
      <div className='container'>
        <ul className='row p-0'>
        {Questions.map((item) => {

          const [index, setIndex] = useState(false);

          const handleClose = useCallback(() => {
            setIndex(false);
          }, []);

return (
            <div  className=' col-12 col-lg-4 col-md-6'>
            <motion.li
              className='layoutItem p-5'
              key={item.item}
              layoutId={`${item.item}`}
              onClick={() => index === false && setIndex(item.item)}
              initial={{ borderRadius: "20px" }}
            >
              <motion.div
                className={styles.placeholder}
                layoutId={`placeholder-${item.item}`}
              >
                <div className='row align-items-center'>
                        <Col className='xs-auto col-2 p-0'>
                            <img className="" alt="Pixie" height="50" width="50" src={imgL} />   
                        </Col>
                        <div className='col-10'>
                            <Title variant="cardLg" className='mb-0'>{`${item.label}`}</Title>
                        </div>
                </div>

                <div />
                <div />
              </motion.div>
              <motion.div
                className={styles.additional}
                layoutId={`additional-${item.item}`}
              >
                <div />
                <div />
                <div />
              </motion.div>
              <motion.div
                className={styles.itemClose}
                layoutId={`close-${item.item}`}
              />
            </motion.li>
            </div>)



          })}
        </ul>



      </div>
    );
  }
