import styled from "styled-components";
import {
  color,
  space,
  border,
  typography,
  shadow,
  flexbox,
  layout,
} from "styled-system";

const ButtonStyled = styled.button`
  font-size: 13px;
  font-weight: 700;
  letter-spacing: 1.63px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 23px 30px;
  box-shadow: 0 32px 54px rgba(3, 3, 3, 0.12);
  border-radius: 50rem;
  display: inline-flex;
  align-items: center;
  justify-content: space-between;
  user-select: none;
  transform: perspective(1px) translateZ(0);
  position: relative;
  overflow: hidden;
  border: none;
  outline: none !important;
  white-space: nowrap;
  background: var(--bs-blue)
  ${border};
  ${color};
  ${space};
  ${typography};
  ${shadow};
  ${flexbox};
  ${layout};
  transition: 0.4s ease-out;
  &:hover,
  &:focus,
  &:active {
    transform: translateY(-10px);
    box-shadow: 0 32px 54px rgba(3, 3, 3, 0.14);
  }
`;

const Button = ({
  color = "#fff",
  arrowRight = false,
  locked = false,
  children,
  ...rest
}) => {
  return (
    <ButtonStyled  color={color} {...rest}>
      {children}
      {arrowRight && (
        <svg
          id="SVGDoc"
          width="18"
          height="12"
          xmlns="http://www.w3.org/2000/svg"
          version="1.1"
          viewBox="0 0 18 12"
          css={`
            margin-left: 1.5rem;
          `}
        >
          <defs></defs>
          <desc>Generated with Avocode.</desc>
          <g>
            <g>
              <title>tail-right</title>
              <g>
                <title>Path</title>
                <path
                  d="M1,5.5h16"
                  fillOpacity="0"
                  fill="#ffffff"
                  strokeLinejoin="round"
                  strokeLinecap="round"
                  strokeOpacity="1"
                  stroke="#ffffff"
                  strokeMiterlimit="20"
                  strokeWidth="2"
                ></path>
              </g>
              <g>
                <title>Path</title>
                <path
                  d="M12,1v0l5,5v0l-5,5v0"
                  fillOpacity="0"
                  fill="#ffffff"
                  strokeLinejoin="round"
                  strokeLinecap="round"
                  strokeOpacity="1"
                  stroke="#ffffff"
                  strokeMiterlimit="20"
                  strokeWidth="2"
                ></path>
              </g>
            </g>
          </g>
        </svg>
      )}
      {locked && (
        <svg 
        width="12px" 
        height="16px" 
        viewBox="0 0 12 16" 
        version="1.1" 
        xmlns="http://www.w3.org/2000/svg"
        className={`
        ms-
      `}
        >
       
          <defs></defs>
          <desc>Locked</desc>
     
      
          <g id="Combined-Shape-2" transform="translate(0.000000, 1.000000)">
              <path d="M6,0 C8.14602074,0 10.5281986,1.90123652 10.6625711,4.2881643 L10.6685309,4.5 L10.6691215,7.11467327 C11.4446338,7.38992278 12,8.13010503 12,9 L12,14 C12,15.1045695 11.1045695,16 10,16 L2,16 C0.8954305,16 1.3527075e-16,15.1045695 0,14 L0,9 C-1.15807333e-16,8.05436136 0.656290633,7.26200762 1.53824108,7.05356958 L1.53878043,4.5 C1.53878043,2.01471863 3.790861,0 6,0 Z M6,9 C5.43007758,9 4.96806417,9.46201341 4.96806417,10.0319358 C4.96806417,10.316897 5.08356752,10.574881 5.27031117,10.7616247 C5.32787951,10.819193 5.22713051,11.5653181 4.96806417,13 L7.03193583,13 C6.77287215,11.5653155 6.67212315,10.8191903 6.72968883,10.7616247 C6.91643248,10.574881 7.03193583,10.316897 7.03193583,10.0319358 C7.03193583,9.46201341 6.56992242,9 6,9 Z M6,1.77498465 C4.34314575,1.77498465 3,3.34314575 3,5 L3,6.99655599 L9,6.99655599 L9,5 C9,3.34314575 7.65685425,1.77498465 6,1.77498465 Z" id="Combined-Shape"></path>
          </g>
          

      </svg>
      )}
    </ButtonStyled>
  );
};

export default Button;
