import styled from "styled-components";
import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
// import imgL from "../../assets/image/portrait-1.png";

import "./styles.css";
import { Suspense, useState } from "react";
import { motion, MotionConfig, useMotionValue } from 'framer-motion/dist/framer-motion';
import { Shapes } from "./Shapes";
import { transition } from "./settings";
import useMeasure from "react-use-measure";


const ImgRight = styled.img`
  max-width: 100%;
`;

function Hero() {

  const [ref, bounds] = useMeasure({ scroll: false });
  const [isHover, setIsHover] = useState(false);
  const [isPress, setIsPress] = useState(false);
  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  const resetMousePosition = () => {
    mouseX.set(0);
    mouseY.set(0);
  };


return (

<>

<Section hero className="position-relative">
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Have a question <br></br>for Papa Bear?
                </Title>

                <Box className='mt-5'>
                  <a href="#questions">
                    <Button arrowRight>Check FAQ </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">
              <motion.button
                        ref={ref}
                        initial={false}
                        animate={isHover ? "hover" : "rest"}
                        whileTap="press"
                        variants={{
                          rest: { scale: 1 },
                          hover: { scale: 1.5 },
                          press: { scale: 1.4 }
                        }}
                        onHoverStart={() => {
                          resetMousePosition();
                          setIsHover(true);
                        }}
                        onHoverEnd={() => {
                          resetMousePosition();
                          setIsHover(false);
                        }}
                        onTapStart={() => setIsPress(true)}
                        onTap={() => setIsPress(false)}
                        onTapCancel={() => setIsPress(false)}
                        onPointerMove={(e) => {
                          mouseX.set(e.clientX - bounds.x - bounds.width / 2);
                          mouseY.set(e.clientY - bounds.y - bounds.height / 2);
                        }}
                      >
                        <motion.div
                          className="shapes"
                          variants={{
                            rest: { opacity: 0 },
                            hover: { opacity: 1 }
                          }}
                        >
                          <div className="pink blush" />
                          <div className="blue blush" />
                          <div className="container">
                            <Suspense fallback={null}>
                              <Shapes
                                isHover={isHover}
                                isPress={isPress}
                                mouseX={mouseX}
                                mouseY={mouseY}
                              />
                            </Suspense>
                          </div>
                        </motion.div>
                        <motion.div
                          variants={{ hover: { scale: 0.85 }, press: { scale: 1.1 } }}
                          className="label"
                        >
                          play
                        </motion.div>
                      </motion.button>
              </div>
            </Col>
          </Row>
        </Container>
      </Section>


</>

);


}


export default Hero;
