import styled from "styled-components";
import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
import { motion, useViewportScroll, useMotionValue, useTransform  } from 'framer-motion/dist/framer-motion';

import imgL from "../../assets/image/portrait-1.png";

const ImgRight = styled.img`
  max-width: 100%;
`;

const Hero = () => {


  const x = useMotionValue(0)
  const y = useMotionValue(0)
  const opacity = useMotionValue(1)

  useEffect(() => {
    function updateOpacity() {
      const maxXY = Math.max(x.get(), y.get())
      const newOpacity = transform(maxXY, [0, 100], [1, 0])
      opacity.set(newOpacity)
    }

    const unsubscribeX = x.onChange(updateOpacity)
    const unsubscribeY = y.onChange(updateOpacity)

    return () => {
      unsubscribeX()
      unsubscribeY()
    }
  }, [])

  return (



    <>
      {/* <!-- Hero Area --> */}
      <Section hero className="position-relative">
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Pixie Bears <br></br>Solana NFT
                </Title>

                <Box className='mt-5'>
                  <a href="#story">
                    <Button arrowRight>Read more </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">

                <motion.div 
                style={{ x }} 
                whileTap={{ scale: 0.9 }}
                whileHover={{ 

                  scale: 1.1 }}
                className="img-main">
                  <ImgRight src={imgL} alt="" />

                </motion.div>

              </div>
            </Col>
          </Row>
        </Container>
      </Section>
    </>
  );
};

export default Hero;
