import React, {useState, useCallback} from "react";
import { motion } from 'framer-motion/dist/framer-motion';
import styles from "styled-components";

import {  Title } from "../core";

import { QuestionsList } from "./Questions";

export default function FaqQuestions() {

    const [index, setIndex] = useState(false);
    const [label, setLabel] = useState(false);
    const [answer, setAnswer] = useState(false);

    const handleClose = useCallback(() => {
      setIndex(false);
    }, []);


    return (
      <div className='container'>
        <ul className='row p-0'>
           {QuestionsList.map((item) => (
            <div  className=' col-12 col-lg-4 col-md-6'>
            <motion.li
              className='layoutItem p-5'
              key={item.id}
              layoutId={`${item.id}`}
              onClick={() => index === false && setIndex(item.id) & setLabel(item.label) & setAnswer(item.answer)


              }
              initial={{ borderRadius: "20px" }}
            >
              <motion.div
                className={styles.placeholder}
                layoutId={`placeholder-${item.id}`}
              >

                <Title variant="cardLg" className='mb-0'>{`${item.label}`}</Title>
              </motion.div>

            </motion.li>
            </div>
          ))}
        </ul>

          {index !== false && (
            <>

                 <div>
              <motion.div className='modalContainer' key="modal">
                <motion.div
                  className='aniModal'
                  layoutId={`${index}`}
                  initial={{ borderRadius: "30px" }}
                >
                  <motion.div
                    className={styles.placeholder}
                    layoutId={`placeholder-${index}`}
                  >
                     <Title variant="cardLg" className='mb-0'>{`${label}`}</Title>
                    <p>{`${answer}`}</p>
                  </motion.div>

                  <motion.div
                    className='modalClose'
                    layoutId={`close-${index}`}
                    onClick={handleClose}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                  >
                    <svg viewBox="0 0 20 20">
                      <path
                        d="M15 5L5 15M5 5l5.03 5.03L15 15"
                        fill="transparent"
                        strokeWidth="2"
                        stroke="currentColor"
                        strokeLinecap="round"
                      />
                    </svg>
                  </motion.div>
                </motion.div>
              </motion.div>
              <motion.div
                className='modalBackdrop'
                key="backdrop"
                onClick={handleClose}
                variants={{
                  hidden: {
                    opacity: 0,
                    transition: {
                      duration: 0.16
                    }
                  },
                  visible: {
                    opacity: 0.8,
                    transition: {
                      delay: 0.04,
                      duration: 0.2
                    }
                  }
                }}
                initial="hidden"
                exit="hidden"
                animate="visible"
              />
              </div>

            </>
          )}

      </div>
    );
  }
