import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
import { motion, useMotionValue } from 'framer-motion/dist/framer-motion';
import imgL from "../../assets/image/portrait-1.png";



function Hero() {

  const y = useMotionValue(0);

return (

<>

      <Section hero className="position-relative" conentOffsetY={y} onScroll={function(){
        consol.log(y.get())
      }}>
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Have a question <br></br>for Papa Bear?
                </Title>

                <Box className='mt-5'>
                  <a href="#questions">
                    <Button arrowRight>Check FAQ </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">
                <motion.div 
                whileTap={{ scale: 0.9 }}
                whileHover={{ 
                  scale: 1.1 }}
                className="img-main">
                  <motion.ImgRight 
                  src={imgL} alt="" />
                  <motion.ImgRight 
                  src={imgL} alt="" />
                  <motion.ImgRight 
                  src={imgL} alt="" />
                  <motion.ImgRight 
                  src={imgL} alt="" />
                </motion.div>
              </div>
            </Col>
          </Row>
        </Container>
      </Section>

</>

);


}


export default Hero;
