import styled from "styled-components";
import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
import { motion } from 'framer-motion/dist/framer-motion';


import imgL from "../../assets/image/portrait-1.png";
import img1 from "../../assets/image/icons/8bitCoin.webp";
import img2 from "../../assets/image/icons/8bitGate.webp";


const ImgRight = styled.img`
  max-width: 100%;
`;




const aniContainer = {
  hidden :{
      opcaity:0.2,
      x: '100vh'
  },

  visible: {
    opacity:1,
    x: 0,
    transitions: {
      type: 'spring',
      delay: 0.5
    }
  }
}



const Hero = () => {
  return (
    <>
      {/* <!-- Hero Area --> */}
      <Section hero className="position-relative">
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Pixie Bears <br></br>Solana NFT
                </Title>

                <Box className='mt-5'>
                  <a href="#story">
                    <Button arrowRight>Read more </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">

                <motion.div 
                variants={aniContainer}
                initial="hidden"
                animate="visible"
                whileTap={{ scale: 0.9 }}
                whileHover={{ 

                  scale: 1.1 }}
                className="img-main">
                  <ImgRight src={imgL} alt="" />
                  <ImgRight src={img1} alt="" />
                  <ImgRight src={img2} alt="" />
                </motion.div>

              </div>
            </Col>
          </Row>
        </Container>
      </Section>
    </>
  );
};

export default Hero;
