import React, {useState} from "react";
import { NavLink } from 'react-router-dom';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { HashLink } from 'react-router-hash-link';
import Container from 'react-bootstrap/Container';
import { motion } from 'framer-motion/dist/framer-motion';

import Logo from "../assets/favicon.png";

function Header() {

    const [show, setShow] = useState(false);
    const showDropdown = (e)=>{
        setShow(!show);
    }
    const hideDropdown = e => {
        setShow(false);
    }


    const Menu = styled.ul`
  @media ${device.lg} {
    display: flex;
    justify-content: flex-end;
  }
  .dropdown-toggle {
    cursor: pointer;
  }
  > li {
    > .nav-link {
      @media ${device.lg} {
        color: ${({ dark, theme }) => theme.colors.front}!important;
        transition: 0.4s;
        align-items: center;
        display: inline-flex;
        font-size: 15px;
        font-weight: 700;
        line-height: 24px;
        padding-top: 18px !important;
        padding-bottom: 18px !important;
        padding-left: 18px !important;
        padding-right: 18px !important;

        text-transform: lowercase;

        &.dropdown-toggle:after {
          margin-left: 10px;
          position: relative;
          transform: rotate(90deg);
          top: 1px;
          content: "\\ea06";
          border: none;
          font-family: "Grayic";
          font-size: 24px;
          transition: 0.4s;
          font-weight: 900;
          line-height: 14px;
        }
      }
    }
    &.nav-item:hover > .nav-link,
    &.nav-item.active > .nav-link {
      color: ${({ theme }) => theme.colors.primary} !important;
      &:after {
        transform: rotate(-90deg);
      }
    }
  }
  .nav-item.dropdown {
    @media ${device.lg} {
      position: relative;
      z-index: 99;
    }
    &:hover {
      > .menu-dropdown {
        @media ${device.lg} {
          top: 90%;
          right: 0;
          opacity: 1;
          pointer-events: visible;
        }
      }
    }
  }
`;

return (


<>
<motion.div>
    <Navbar collapseOnSelect expand="lg" fixed="top" bg="light">
    <Container>
    <HashLink to="/#" className='navbar-brand'> 
    <img
          alt="Pixie Bears"
          src={Logo}
          width="30"
          height="30"
          className="d-inline-block align-middle"
        />{' '}
      <span className="d-inline-block align-middle">Pixie Bears</span>
    </HashLink> 

    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
    <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="me-auto">
        </Nav>
        <Nav>
            <NavDropdown title="about" id="basic-nav-dropdown" href="/"
            show={show}
            onMouseEnter={showDropdown} 
            onMouseLeave={hideDropdown}
            >
            <HashLink className='dropdown-item' smooth to='/#story' >story</HashLink>
            <HashLink className='dropdown-item' smooth to='/#roadmap' >roadmap</HashLink>
            <HashLink className='dropdown-item' smooth to='/#team' >team</HashLink>
            <HashLink className='dropdown-item' smooth to='/#contact' >collaboration</HashLink>
            </NavDropdown>

            <NavLink className='nav-item nav-link disabled' to='/rarity'>rarity</NavLink>
            <NavLink className='nav-item nav-link disabled' to='/mint'>mint</NavLink>
            <NavLink className='nav-item nav-link disabled' to='/staking'>staking</NavLink>
            <NavLink className='nav-item nav-link disabled' to='/merch'>merch</NavLink>
           <NavLink className='nav-item nav-link' to='/faq'>FAQ</NavLink>
        </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
</motion.div>
</>


);


}


export default Header;
