import React, {useState} from "react";
import { NavLink } from 'react-router-dom';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { HashLink } from 'react-router-hash-link';
import Container from 'react-bootstrap/Container';
import { motion } from 'framer-motion/dist/framer-motion';

import Logo from "../assets/favicon.png";

function Header() {

    const [show, setShow] = useState(false);
    const showDropdown = (e)=>{
        setShow(!show);
    }
    const hideDropdown = e => {
        setShow(false);
    }

return (


<>
<motion.div>
    <Navbar collapseOnSelect expand="lg" fixed="top" bg="light">
    <Container>
    <HashLink to="/" className='navbar-brand'> 
    </HashLink> 
    <Navbar.Brand href="/"> 
     <img
          alt="Pixie Bears"
          src={Logo}
          width="30"
          height="30"
          className="d-inline-block align-middle"
        />{' '}
      <span className="d-inline-block align-middle">Pixie Bears</span></Navbar.Brand>
    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
    <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="me-auto">
        </Nav>
        <Nav>
            <NavDropdown title="About" id="basic-nav-dropdown" href="/"
            show={show}
            onMouseEnter={showDropdown} 
            onMouseLeave={hideDropdown}
            >
            <HashLink className='dropdown-item' smooth to='/#story' >Story</HashLink>
            <HashLink className='dropdown-item' smooth to='/#roadmap' >Roadmap</HashLink>
            <HashLink className='dropdown-item' smooth to='/#team' >Team</HashLink>
            <HashLink className='dropdown-item' smooth to='/#contact' >Colaboration</HashLink>
            </NavDropdown>

            <NavLink className='nav-item nav-link' to='/rarity'>Rarity</NavLink>
            <NavLink className='nav-item nav-link' to='/mint'>Mint</NavLink>
            <NavLink className='nav-item nav-link' to='/staking'>Staking</NavLink>
            <NavLink className='nav-item nav-link' to='/merch'>Merch</NavLink>
           <NavLink className='nav-item nav-link' to='/faq'>FAQ</NavLink>
        </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
</motion.div>
</>


);


}


export default Header;
