import styled from "styled-components";
import { Container, Row, Col } from "react-bootstrap";

import { Title, Button, Section, Box } from "../core";
import { motion, useViewportScroll, useTransform } from 'framer-motion/dist/framer-motion';
//import { useInView } from 'react-intersection-observer';

import imgL from "../../assets/image/portrait-1.png";

const ImgRight = styled.img`
  max-width: 100%;
`;

const Hero = () => {

  const { scrollY } = useViewportScroll();
  const y1 = useTransform(scrollY, [0, 200], [0, 10]);
  const y2 = useTransform(scrollY, [0, 100], [0, 20]);


  return (


    <>
      {/* <!-- Hero Area --> */}
      <Section hero className="position-relative">
        <Container>
          <Row className="align-items-center">
            <Col lg="7" className="mb-5 mb-lg-0">
              <Box>
                <Title variant="hero">
                 Pixie Bears <br></br>Solana NFT
                </Title>

                <Box className='mt-5'>
                  <a href="#story">
                    <Button arrowRight>Read more </Button>
                  </a>
                </Box>
              </Box>
            </Col>
            <Col lg="5" md="8" sm="9">
              <div className="text-center text-lg-right position-relative">


              <motion.div className="box" style={{ y: y1 }} >
                    <ImgRight src={imgL} alt="" />
              </motion.div>

              <motion.div
                className="box"
                style={{ y: y2 }}
              >
                <ImgRight src={imgL} alt="" />
              </motion.div>


              </div>
            </Col>
          </Row>
        </Container>
      </Section>
    </>
  );
};

export default Hero;
