import React, {useState, useCallback} from "react";
import { motion } from 'framer-motion/dist/framer-motion';
import styles from "styled-components";

import {  Title } from "../core";

//import { Questions } from "./Questions";

export default function App() {


    return (
      <div className='container'>
        <ul className='row p-0'>
        {[ 1, 2, 3, 4, 5, 6 ].map((item) => (
            <div  className=' col-12 col-lg-4 col-md-6'>
            <motion.li
              className='layoutItem p-5'
              key={item}
              layoutId={`${item}`}
              onClick={() => index === false && setIndex(item)}
              initial={{ borderRadius: "20px" }}
            >
              <motion.div
                className={styles.placeholder}
                layoutId={`placeholder-${item}`}
              >
                <Title variant="cardLg" className='mb-0'>{`placeholder-${item}`}</Title>
                <div />
                <div />
              </motion.div>
              <motion.div
                className={styles.additional}
                layoutId={`additional-${item}`}
              >
                <div />
                <div />
                <div />
              </motion.div>
              <motion.div
                className={styles.itemClose}
                layoutId={`close-${item}`}
              />
            </motion.li>
            </div>
          ))}
        </ul>

          {index !== false && (
            <>

                 <div>
              <motion.div className='modalContainer' key="modal">
                <motion.div
                  className='aniModal'
                  layoutId={`${index}`}
                  initial={{ borderRadius: "30px" }}
                >
                  <motion.div
                    className={styles.placeholder}
                    layoutId={`placeholder-${index}`}
                  >
                    <h1>Test</h1>
                    <div />
                    <div />
                  </motion.div>
                  <motion.div
                    className={styles.additional}
                    layoutId={`additional-${index}`}
                  >
                    <div />
                    <div />
                    <div />
                  </motion.div>
                  <motion.div
                    className='modalClose'
                    layoutId={`close-${index}`}
                    onClick={handleClose}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                      <path
                        d="M15 5L5 15M5 5l5.03 5.03L15 15"
                        fill="transparent"
                        strokeWidth="2"
                        stroke="currentColor"
                        strokeLinecap="round"
                      />
                    </svg>
                  </motion.div>
                </motion.div>
              </motion.div>
              <motion.div
                className='modalBackdrop'
                key="backdrop"
                onClick={handleClose}
                variants={{
                  hidden: {
                    opacity: 0,
                    transition: {
                      duration: 0.16
                    }
                  },
                  visible: {
                    opacity: 0.8,
                    transition: {
                      delay: 0.04,
                      duration: 0.2
                    }
                  }
                }}
                initial="hidden"
                exit="hidden"
                animate="visible"
              />
              </div>

            </>
          )}

      </div>
    );
  }
